    private void tb_codigosMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tb_codigosMouseClicked
        int Linha = tb_codigos.getSelectedRow();
        
        c_nivelacesso.setSelectedItem(tb_codigos.getValueAt(Linha, 1).toString());
        c_usos.setText(tb_codigos.getValueAt(Linha, 2).toString());
        c_codigo.setText(tb_codigos.getValueAt(Linha, 3).toString());
    }//GEN-LAST:event_tb_codigosMouseClicked

