    private void b_apagarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_apagarActionPerformed
        CONTROLLER.GerenciarCodigosC GerenciarCodigosC = new CONTROLLER.GerenciarCodigosC();
        int Linha = tb_codigos.getSelectedRow();
        
        String ID = tb_codigos.getValueAt(Linha, 0).toString();
        
        boolean Deletado = GerenciarCodigosC.Deletar(Integer.parseInt(ID));
        
        if(Deletado == true){
            String Procurar = c_procurar.getText().toString();
            String Filtro = c_infos_filtro.getSelectedItem().toString();

            c_usos.setText("");
            c_codigo.setText("");
            c_nivelacesso.setSelectedItem("2");

            tb_codigos.setModel(GerenciarCodigosC.ListarCodigos(Procurar, Filtro));
            JOptionPane.showMessageDialog(null, "Deletado com sucesso!", "Status", JOptionPane.INFORMATION_MESSAGE);
        } else {
            JOptionPane.showMessageDialog(null, "Erro ao deletar, tente novamente mais tarde!", "Erro", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_b_apagarActionPerformed

    private void b_adicionarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_adicionarActionPerformed
        CONTROLLER.GerenciarCodigosC GerenciarCodigosC = new CONTROLLER.GerenciarCodigosC();
        
        String NivelAcesso = c_nivelacesso.getSelectedItem().toString();
        String Usos = c_usos.getText();
        String Codigo = c_codigo.getText().toString();
        
        if(Codigo.length() > 7 || Codigo.length() < 6){
            JOptionPane.showMessageDialog(null, "O c贸digo deve ter at茅 6 dig铆tos!", "Erro", JOptionPane.INFORMATION_MESSAGE);
        } else {
            if (Usos.length() > 4 || Usos.equals("") || Integer.parseInt(Usos) > 1000 || Integer.parseInt(Usos) < 0){
                JOptionPane.showMessageDialog(null, "Os usos deve ter at茅 4 dig铆tos entre 0 e 1000!", "Erro", JOptionPane.INFORMATION_MESSAGE);
            } else{
                boolean Adicionado = GerenciarCodigosC.Adicionar(Integer.parseInt(Usos), Integer.parseInt(NivelAcesso), Codigo);

                if(Adicionado == true){
                    String Procurar = c_procurar.getText().toString();
                    String Filtro = c_infos_filtro.getSelectedItem().toString();

                    c_usos.setText("");
                    c_codigo.setText("");
                    c_nivelacesso.setSelectedItem("2");

                    tb_codigos.setModel(GerenciarCodigosC.ListarCodigos(Procurar, Filtro));
                    JOptionPane.showMessageDialog(null, "Adicionado com sucesso!", "C贸digo adicionado", JOptionPane.INFORMATION_MESSAGE);
                } else{
                    JOptionPane.showMessageDialog(null, "Erro ao atualizar, tente novamente mais tarde!", "Erro", JOptionPane.INFORMATION_MESSAGE);
                }
            }
        }
    }//GEN-LAST:event_b_adicionarActionPerformed

